const fetchCartItems = async (userId) => {
  try {
    const cartRef = doc(db, "carts", userId);
    const cartSnap = await getDoc(cartRef);
    
    if (!cartSnap.exists()) {
      // Initialize cart if it doesn't exist
      await setDoc(cartRef, {
        items: [],
        createdAt: new Date(),
        updatedAt: new Date(),
        total: 0
      });
      return [];
    }

    const cartData = cartSnap.data();
    const items = cartData.items || [];
    
    // Filter out any invalid items
    const validItems = items.filter(item => 
      item && item.productId && typeof item.quantity === 'number'
    );

    const productIds = validItems.map(item => item.productId);
    
    if (productIds.length === 0) {
      return [];
    }

    const productsQuery = query(
      collection(db, "products"),
      where("__name__", "in", productIds)
    );
    
    const productsSnap = await getDocs(productsQuery);
    const productsMap = {};
    
    productsSnap.forEach(doc => {
      productsMap[doc.id] = doc.data();
    });

    // Merge cart items with product data
    const mergedItems = validItems.map(item => ({
      ...item,
      product: productsMap[item.productId] || {
        id: item.productId,
        name: "Product not found",
        price: 0
      }
    }));
    
    setCartItems(mergedItems);
    return mergedItems;
    
  } catch (error) {
    console.error("Error fetching cart items:", error);
    setCartItems([]);
    return [];
  }
};

// Add to cart with validation
const handleAddToCart = async (product) => {
  try {
    if (!product || !product.id) {
      throw new Error("Invalid product");
    }

    if (!auth.currentUser) {
      navigate('/login');
      return false;
    }

    const userId = auth.currentUser.uid;
    const cartRef = doc(db, "carts", userId);
    const cartSnap = await getDoc(cartRef);
    
    let items = [];
    if (cartSnap.exists()) {
      items = [...(cartSnap.data().items || [])];
    } else {
      // Initialize cart if it doesn't exist
      await setDoc(cartRef, {
        items: [],
        createdAt: new Date(),
        updatedAt: new Date(),
        total: 0
      });
    }

    // Validate product data
    const safeProduct = {
      productId: product.id,
      quantity: 1,
      price: Number(product.price) || 0,
      name: String(product.name) || "Unnamed Product",
      image: product.image || ""
    };

    const existingIndex = items.findIndex(item => 
      item && item.productId === product.id
    );

    if (existingIndex >= 0) {
      items[existingIndex].quantity += 1;
    } else {
      items.push(safeProduct);
    }

    // Calculate total
    const total = items.reduce((sum, item) => {
      return sum + ((item.price || 0) * (item.quantity || 0));
    }, 0);

    await updateDoc(cartRef, {
      items: items.filter(item => item), // Remove any null/undefined
      updatedAt: new Date(),
      total: total
    });

    // Refresh cart items
    await fetchCartItems(userId);
    return true;
  } catch (error) {
    console.error("Error adding to cart:", error);
    return false;
  }
};

// Handle quantity change with validation
const handleQuantityChange = async (productId, change) => {
  if (!auth.currentUser) {
    navigate('/login');
    return;
  }
  
  try {
    const userId = auth.currentUser.uid;
    const cartRef = doc(db, 'carts', userId);
    const cartSnap = await getDoc(cartRef);
    
    if (!cartSnap.exists()) {
      await setDoc(cartRef, {
        items: [],
        createdAt: new Date(),
        updatedAt: new Date(),
        total: 0
      });
      return;
    }
    
    const cartData = cartSnap.data();
    const items = [...(cartData.items || [])];
    const itemIndex = items.findIndex(item => 
      item && item.productId === productId
    );
    
    if (itemIndex === -1) return;
    
    const updatedItems = [...items];
    const currentItem = updatedItems[itemIndex];
    const newQuantity = (currentItem.quantity || 0) + change;
    
    if (newQuantity <= 0) {
      updatedItems.splice(itemIndex, 1);
    } else {
      updatedItems[itemIndex] = {
        ...currentItem,
        quantity: newQuantity
      };
    }
    
    // Calculate new total
    const total = updatedItems.reduce((sum, item) => {
      return sum + ((item.price || 0) * (item.quantity || 0));
    }, 0);
    
    await updateDoc(cartRef, {
      items: updatedItems.filter(item => item), // Ensure no undefined
      updatedAt: new Date(),
      total: total
    });
    
    // Refresh cart items
    await fetchCartItems(userId);
    
  } catch (error) {
    console.error("Error updating quantity:", error);
  }
};

// Remove item from cart
const removeFromCart = async (productId) => {
  if (!auth.currentUser) {
    navigate('/login');
    return;
  }
  
  try {
    const userId = auth.currentUser.uid;
    const cartRef = doc(db, 'carts', userId);
    const cartSnap = await getDoc(cartRef);
    
    if (!cartSnap.exists()) return;
    
    const cartData = cartSnap.data();
    const items = [...(cartData.items || [])];
    const updatedItems = items.filter(item => item.productId !== productId);
    
    // Calculate new total
    const total = updatedItems.reduce((sum, item) => {
      return sum + ((item.price || 0) * (item.quantity || 0));
    }, 0);
    
    await updateDoc(cartRef, {
      items: updatedItems,
      updatedAt: new Date(),
      total: total
    });
    
    // Refresh cart items
    await fetchCartItems(userId);
    
  } catch (error) {
    console.error("Error removing item from cart:", error);
  }
};

